import React from 'react';
import Login from '../Login/Login';
import RegisterUser from '../RegisterUser/RegisterUser';
import styles from '../StyleSheet/Modal.module.css';

function Modal({ onClose, children }) {
  const [showRegisterForm, setShowRegisterForm] = useState(false);

  const onRegister = () => {
    setShowRegisterForm(true);
  }
  
  const onLogin = () => {
    setShowRegisterForm(false);
  }

  const registerForm = showRegisterForm ? <RegisterUser /> : null;
  const loginForm = showRegisterForm ? null : <Login />;

  return (
    <div className={styles.overlay}>
      <div className={styles.modal}>
        <button className={styles.closeButton} onClick={onClose}>
        ‚ùå
        </button>
        <div className={styles.modalContent}>
          {showRegisterForm ? registerForm : loginForm}
        </div>
        <div className={styles.buttonsContainer}>
          <button className={styles.registerButton} onClick={onRegister}>
            Registrarme
          </button>
          <button className={styles.loginButton} onClick={onLogin}>
            Ya estoy registrado
          </button>
        </div>
      </div>
    </div>
  );
}
