import React, { useRef, useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import Styles from "../StyleSheet/Navbar.module.css";

export const NavBar = () => {
  const hamburguerRef = useRef(null);
  const navMenuRef = useRef(null);
  

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        navMenuRef.current &&
        !navMenuRef.current.contains(event.target) &&
        hamburguerRef.current &&
        !hamburguerRef.current.contains(event.target)
      ) {
        hamburguerRef.current.classList.remove("active");
        navMenuRef.current.classList.remove("active");
      }
    };

    document.addEventListener("click", handleClickOutside);

    return () => {
      document.removeEventListener("click", handleClickOutside);
    };
  }, [hamburguerRef, navMenuRef]);

  const handleHamburguerClick = () => {
    hamburguerRef.current.classList.toggle("active");
    navMenuRef.current.classList.toggle("active");
  };

    return(
        <nav className={Styles["nav-container"]}>
        <ul className={Styles["nav-menu"]} ref={navMenuRef}>
            <img src="https://res.cloudinary.com/dsjsbp6qy/image/upload/v1679065705/Dshop/Dise%C3%B1o_sin_t%C3%ADtulo__19_-removebg-preview-removebg-preview_gvpsgd.png" 
            alt="logo"
            width={300}
            height={100}/>
            <div>
          <NavLink className={Styles["nav-link"]} exact to="/" activeClassName="active">
            Inicio
          </NavLink>
          <NavLink className={Styles["nav-link"]} exact to="/cursos" activeClassName="active">
            Cursos
          </NavLink>

          <NavLink
          className={Styles["nav-link"]}
            activeClass="active"
            to=""
            spy={true}
            smooth={true}
            offset={-70}
            duration={500}
          >
            Solicitar ayuda
          </NavLink>
          </div>
        </ul>
        <div
          className="hamburguer"
          ref={hamburguerRef}
          onClick={handleHamburguerClick}
        >
          <span className="bar"></span>
          <span className="bar"></span>
          <span className="bar"></span>
        </div>
      </nav>
    )
}

export default NavBar;
