import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { getUserByCode, loginSuccess } from "../../Redux/actions"; 
import styles from "../StyleSheet/Login.module.css";
import Swal from 'sweetalert2';
import 'sweetalert2/dist/sweetalert2.min.css';

function Login() {
  const [code, setCode] = useState("");
  const [showModal, setShowModal] = useState(true); 
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const dispatch = useDispatch();

  const handleCodeChange = (event) => {
    setCode(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const user = await dispatch(getUserByCode(code));
      if (user) {
        dispatch(loginSuccess(user));
        setIsAuthenticated(true);
        setShowModal(false);
        Swal.fire({
          icon: 'success',
          title: 'Inicio de sesión exitoso',
          showConfirmButton: false,
          timer: 1800
        });
      } else {
        setIsAuthenticated(false);
        Swal.fire({
          icon: 'error',
          title: 'Usuario no encontrado',
          text: 'El usuario que intentas ingresar no existe o es inválido',
          confirmButtonText: 'Aceptar'
        });
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      {showModal && (
        <div className={styles.modal}>
          <div className={styles.modalContent}>
            <form onSubmit={handleSubmit}>
              <div className={styles["login-container"]}>
                <label htmlFor="code">Ingresa tu código:</label>
                <input type="text" id="code" value={code} onChange={handleCodeChange} />
              </div>
              <button className={styles["login-button"]} type="submit">
                Ingresar
              </button>
            </form>
          </div>
        </div>
      )}
      {!showModal && isAuthenticated && (
        <div className={styles.loggedIn}>
          <p>Bienvenido, {user.name}</p>
          <button onClick={() => setIsAuthenticated(false)}>Cerrar sesión</button>
        </div>
      )}
      {!showModal && !isAuthenticated && (
        <div className={styles.loggedOut}>
          <button onClick={() => setShowModal(true)}>Iniciar sesión</button>
        </div>
      )}
    </div>
  );
}

export default Login;
