import React, { useRef, useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import "./navStyle.module.css"

export const NavBar = () => {
  const hamburguerRef = useRef(null);
  const navMenuRef = useRef(null);
  

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        navMenuRef.current &&
        !navMenuRef.current.contains(event.target) &&
        hamburguerRef.current &&
        !hamburguerRef.current.contains(event.target)
      ) {
        hamburguerRef.current.classList.remove("active");
        navMenuRef.current.classList.remove("active");
      }
    };

    document.addEventListener("click", handleClickOutside);

    return () => {
      document.removeEventListener("click", handleClickOutside);
    };
  }, [hamburguerRef, navMenuRef]);

  const handleHamburguerClick = () => {
    hamburguerRef.current.classList.toggle("active");
    navMenuRef.current.classList.toggle("active");
  };

    return(
        <nav className={StyleSheet["nav-container"]}>
        <ul className="nav-menu" ref={navMenuRef}>
            <img src="https://res.cloudinary.com/dsjsbp6qy/image/upload/v1679063828/Dshop/logo-portamor_pnztoi.png" alt="logo"/>
          <NavLink exact to="/" activeClassName="active">
            Inicio
          </NavLink>
          <NavLink exact to="/cursos" activeClassName="active">
            Cursos
          </NavLink>

          <NavLink
            activeClass="active"
            to=""
            spy={true}
            smooth={true}
            offset={-70}
            duration={500}
          >
            Solicitar ayuda
          </NavLink>
        </ul>
        <div
          className="hamburguer"
          ref={hamburguerRef}
          onClick={handleHamburguerClick}
        >
          <span className="bar"></span>
          <span className="bar"></span>
          <span className="bar"></span>
        </div>
      </nav>
    )
}

export default NavBar;
