import React, { useRef, useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import Styles from "../StyleSheet/Navbar.module.css";

export const NavBar = () => {
  const hamburguerRef = useRef(null);
  const navMenuRef = useRef(null);
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        navMenuRef.current &&
        !navMenuRef.current.contains(event.target) &&
        hamburguerRef.current &&
        !hamburguerRef.current.contains(event.target)
      ) {
        hamburguerRef.current.classList.remove(Styles.active);
        navMenuRef.current.classList.remove(Styles.active);
        setMenuOpen(false);
      }
    };
    const handleSalirMenuClick = () => {
      setMenuOpen(false);
    };

    document.addEventListener("click", handleClickOutside);

    return () => {
      document.removeEventListener("click", handleClickOutside);
    };
  }, [hamburguerRef, navMenuRef]);

  const handleHamburguerClick = () => {
    setMenuOpen(!menuOpen);
    hamburguerRef.current.classList.toggle(Styles.active);
    navMenuRef.current.classList.toggle(Styles.active);
  };

  const handleSalirMenuClick = () => {
    setMenuOpen(false);
    hamburguerRef.current.classList.remove(Styles.active);
    navMenuRef.current.classList.remove(Styles.active);
  };

  return (
    <nav className={Styles["nav-container"]}>
      <img
        src="https://res.cloudinary.com/dsjsbp6qy/image/upload/v1679065705/Dshop/Dise%C3%B1o_sin_t%C3%ADtulo__19_-removebg-preview-removebg-preview_gvpsgd.png"
        alt="logo"
        width={300}
        height={100}
      />
      <ul
        className={`${Styles["nav-menu"]} ${menuOpen ? Styles["active"] : ""}`}
        ref={navMenuRef}
      >
        <ul className={Styles["container-link"]}>
          <li>
            <NavLink
              className={Styles["nav-link"]}
              exact
              to="/"
              activeClassName="active"
            >
              Inicio
            </NavLink>
          </li>
          <li>
            <NavLink
              className={Styles["nav-link"]}
              exact
              to="/cursos"
              activeClassName="active"
            >
              Cursos
            </NavLink>
          </li>
          <li>
            <NavLink className={Styles["nav-link"]} activeClass="active" to="">
              Solicitar ayuda
            </NavLink>
          </li>
          <li>
            <button
              className={Styles["button-salirMenu"]}
              onClick={handleSalirMenuClick}
            >
              Salir del Menú
            </button>
          </li>
        </ul>
      </ul>
      <div
        className={`${Styles["hamburguer"]} ${
          menuOpen ? Styles["active"] : ""
        }`}
        ref={hamburguerRef}
        onClick={handleHamburguerClick}
      >
        <button className={Styles["bar"]}>Menú</button>
      </div>
    </nav>
  );
};

export default NavBar;
