const initialState = {
  courses: [],
  user: {},
  session: null,
  error: null,
};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    case 'GET_COURSES':
      return {
        ...state,
        courses: action.payload,
      };
    case 'LOGIN_SUCCESS':
      return {
        ...state,
        session: action.payload,
        error: null,
      };
    case 'LOGIN_FAIL':
      return {
        ...state,
        error: action.payload,
      };
    default:
      return state;
  }
}

export default rootReducer;
