import React  from "react";
import { NavLink, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { getCoursesById } from "../../Redux/actions";
import styles from "./CourseDetail.module.css";

export const CourseDetail = (props) => {

  const dispatch = useDispatch();
  const {id} = useParams();
  const course = useSelector((state) => state.courses);

  useEffect(() => {
    console.log("id:", id);
    dispatch(getCoursesById(id));
  }, [dispatch, id]);
  

  return (
    <div className={styles["course-detail-main"]}>
      <h1>{course.title}</h1>

      <div className={styles["course-detail-image-description"]}>
        <div className={styles["course-img-container"]}>
          <img
            className={styles["course-img"]}
            src={course.image}
            alt="detail-course"
          />
          <strong className={styles["course-genre"]}>
            {course.genre}
          </strong>
        </div>

        <div className={styles["right-detail-course"]}>
          <p className={styles["course-description"]}>
            {course.description}
          </p>
          <p className={styles["instructor-container"]}>
            Tallerista: <strong>{course.instructor}</strong>
          </p>
          <div className={styles["course-buttons-container"]}>
            <NavLink
              to="#"
              className={styles["inscription-button"]}
             
            >
              Inscribete Aquí
            </NavLink>
            <div className={styles["help-container"]}>
              <span>¿Necesitas ayuda?</span>
              <NavLink
                to={`https://wa.me/123456`}
                className={styles["help-button"]}
              >
                Pide ayuda aqui
              </NavLink>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CourseDetail;
