import axios from "axios"

export function getCourses() {
    return async function (dispatch) {
      try {
        var json = await axios.get("http://localhost:3001/courses");
        return dispatch({
          type: "GET_COURSES",
          payload: json.data,
        });
      } catch (error) {
        console.log("Error en getCourses/actions", error);
      }
    };
  }

  export function postUser (payload){
    return async function (dispatch){
        const response = await axios.post('http://localhost:3001/users', payload);
        return response;
    }
  }
  export const getUserbyCode = (code) => async (dispatch) => {
    try {
      const response = await axios.get(`http://localhost:3001/users?code=${code}`);
      const user = response.data[0];
      localStorage.setItem('user', JSON.stringify(user));
      dispatch({
        type: 'LOGIN_SUCCESS',
        payload: user,
      });
      return user; // Devuelve solo el objeto de usuario
    } catch (error) {
      dispatch({
        type: 'LOGIN_FAIL',
        payload: { message: error.message },
      });
      throw error;
    }
  };
  

  export function setSession(session) {
    return {
      type: 'SET_SESSION',
      payload: session
    }
  }
