const initialState = {
   courses:[],
   user:[],
   session: null
  };
  
  function rootReducer(state = initialState, action) {
    switch (action.type) {
  
      case "GET_COURSES":
        return {
          ...state,
          courses: action.payload,
        };
        case "LOGIN_SUCCESS":
          return {
            ...state,
            user: action.payload,
          };
          case 'SET_SESSION':
            return {
              ...state,
              session: action.payload
            }
      
  
        default:
          return state;
    }
  }
  
  export default rootReducer;
